<script>
    function qing()
    {
        var member = "{{ $_member }}";
        showMsg("抢红包…");
        if(!member){
            showMsg("{{ __('ft.please_log_in_first') }}");
        }
        var url = "{{ route('member.distillRed') }}";
        $.get(url,function(data)
        {
            if (data.status.errorCode == 0){
                var m = data.data;
                showMsg("抢到红包<strong>"+m+"</strong>元")
            }else {
                var html = '';
                if (!typeof (data.status.msg) == 'string')
                {
                    html+= data.status.msg;
                }else {
                    var obj = JSON.parse (data.status.msg);
                    for ( var p in obj )
                    {
                        if (typeof (obj[p]) == 'string')
                        {
                            html+= '<p><b>'+ obj[p] + '</b></p>';
                        } else if(obj[p] instanceof Array)
                        {
                            for (var i=0;i<obj[p].length;i++)
                            {
                                html+= '<p><b>'+ obj[p][i] + '</b></p>';
                            }

                        }
                    }
                }
                showMsg(html);
            }
            /*返回Json对象
            ---Result---
            0:成功
            1:参数错误
            2:未登陆
            3:抢红包失败

            ---Message---
            消息内容
            */
            /*if(data["Result"]==0)
            {
                showMsg("抢到红包<strong>"+data["Message"]+"</strong>元")
            }
            else
            {
                showMsg(data["Message"])
            }*/
        })
    }
</script>
